var fecha = msg.payload.Date; 
var hora = msg.payload.Time; 
var Gap = msg.payload.Gap;
var Grp = msg.payload.Grp;
var Voltage = msg.payload.Voltage;
var Gi = msg.payload.Gi;
var Sm1 = msg.payload.Sm1;
var Sm2 = msg.payload.Sm2;
var Sm3 = msg.payload.Sm3;

var params = [fecha, hora, Gap, Grp, Voltage, Gi, Sm1, Sm2, Sm3];

msg.params = params;

return msg;

var idateMillis = msg.payload.idate;
var fdateMillis = msg.payload.fdate;

var idate = new Date(idateMillis)
                .toISOString().slice(0, 10);
var fdate = new Date(fdateMillis)
                .toISOString().slice(0, 10);

msg.params = [idate,fdate];

return msg;

const datos = msg.payload;
const datosPorDia = {};
const labels = [], data = [], series = [];
const Gap = [];

// Iterar sobre cada fila en los resultados
datos.forEach((fila) => {

    // Obtener el nombre del día (sin la hora) de la fecha
    const fecha = new Date(fila.Date);

    // Formatear la fecha en formato "dd/mm/aa"
    const dia = fecha.getDate().toString().padStart(2, '0');
    const mes = (fecha.getMonth() + 1).toString().padStart(2, '0');
    const anio = fecha.getFullYear().toString().slice(-2);
    const claveDia = `${dia}/${mes}/${anio}`;

    // Si aún no existe una entrada para este día, inicializar el valor acumulado y el contador
    if (!datosPorDia[claveDia]) {
        datosPorDia[claveDia] = { total: 0, contador: 0 };
    }

    // Actualizar el valor acumulado y el contador para este día
    datosPorDia[claveDia].total += fila.Gap;
    datosPorDia[claveDia].contador++;

});

// Calcular el promedio de Global_active_power para cada día y almacenar en un objeto JSON
Object.keys(datosPorDia).forEach((claveDia) => {
    const promedio = datosPorDia[claveDia].total / datosPorDia[claveDia].contador;
    Gap.push(promedio);
    labels.push(claveDia);
});

series.push("Gap");
data.push(Gap);

const datosJSON = {
    "labels":labels,
    "data":data,
    "series":series
};


// Convertir el objeto JSON a una cadena JSON
const datosJSONString = JSON.stringify(datosJSON);
msg.payload = [datosJSON]
return msg;

